    public static void CreateRoom(Vector2 roomCenter, string levelName) {
        // string json = File.ReadAllText(Application.dataPath + "/LevelsJSONData/" + levelName);

        string json = GetLevelData(levelName);
        LevelObjects levelData = JsonUtility.FromJson<LevelObjects>(json);

        foreach (LevelObject obj in levelData.objects) {
            GameObject toCreate = Resources.Load(obj.prefabPath, typeof(GameObject)) as GameObject;
            Instantiate(toCreate, roomCenter + obj.position, Quaternion.identity);
        }
    }

